update checkpoint set last_modified_date = current_timestamp, name = 'Use of energy efficient bulbs', means_of_verification = 'Check that PHC uses energy efficient bulb like CFL or LED for lighting purpose within the PHC Premises' where id = 13488;
update checkpoint set last_modified_date = current_timestamp, name = 'Adequate illumination in inside and outside of the PHC area', means_of_verification = 'Check for Adequate lighting arrangements through Natural Light or Electric Bulbs inside PHC. Check that PHC front, entry gate and access road are well illuminated' where id = 13489;
update checkpoint set last_modified_date = current_timestamp, name = 'No water logging in open areas', means_of_verification = 'Check for water accumulation in open areas because of faulty drainage, leakage from the pipes, etc.' where id = 13490;
update checkpoint set last_modified_date = current_timestamp, name = 'There is no abandoned / dilapidated building / unused structure within the premises', means_of_verification = 'Check for presence of any ‘abandoned building’ and unused temporary structure within the premises' where id = 13491;
update checkpoint set last_modified_date = current_timestamp, name = 'No junk material within PHC premises', means_of_verification = 'Check if unused/ condemned articles, and outdated records are kept in the Nursing stations, OPD clinics, Labour Room , Injection Room , Dressing Room, Wards, stairs, open areas, roof tops, balcony etc.' where id = 13492;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC has demarcated space for keeping condemned junk material', means_of_verification = 'Check for availability of a demarcated & secured space for collecting and storing the junk material before its disposal' where id = 13493;
update checkpoint set last_modified_date = current_timestamp, name = 'Walls are well-plastered and painted', means_of_verification = 'Check that wall (Internal and External) plaster is not chipped-off and the building is painted/ whitewashed in approved colour scheme. The paint has not faded away. Check for presence of any outdated posters & boards' where id = 13494;
update checkpoint set last_modified_date = current_timestamp, name = 'Name of the PHC is prominently displayed at the entrance and have uniform signage system', means_of_verification = 'Name of the PHC is prominently displayed as per state’s policy.The name board of the facility is well illuminated in night or is florescent.Check All signage''s (directional & departmental) are in local language and follow uniform colour scheme' where id = 13495;
update checkpoint set last_modified_date = current_timestamp, name = 'Check if the facility has rain-water harvesting system', means_of_verification = 'Check for its functionality and storage system' where id = 13496;
update checkpoint set last_modified_date = current_timestamp, name = 'Water supply system is maintained in the PHC', means_of_verification = 'Check for leaking taps, pipes, over-flowing tanks and dysfunctional cisterns.Over-head tank has functional float-valve.' where id = 13497;
update checkpoint set last_modified_date = current_timestamp, name = 'No stray animals within the facility premises', means_of_verification = 'Observe for the presence of stray animals such as dogs, cats, cattle, pigs, etc. within the premises. Also discuss with the facility staff.Check at the entrance of facility that cattle trap has been provided. Also look at the breach, if any, in the boundary wall' where id = 13498;
update checkpoint set last_modified_date = current_timestamp, name = 'Pest Control Measures are implemented in the facility', means_of_verification = 'Check for the evidence at the facility ( Presence of Pests ,Record of Purchase of Pesticides and availability of the rat trap) and interview the staff' where id = 13499;
update checkpoint set last_modified_date = current_timestamp, name = 'The Staff periodically sorts useful and unnecessary articles at work station', means_of_verification = 'Ask the staff, how frequently they sort and remove unnecessary articles from their work place like Nursing stations, work bench, dispensing counter in Pharmacy, etc.Check for presence of unnecessary articles.' where id = 13500;
update checkpoint set last_modified_date = current_timestamp, name = 'The Staff arranges the useful articles, records in systematic manner and label them', means_of_verification = 'Check if drugs, instruments, records are not lying in haphazard manner and kept near to point of use in systematic manner. The place has been demarcated for keeping different articlesCheck that drugs, instruments, records, etc. are labelled for facilitating easy identification.' where id = 13501;
update checkpoint set last_modified_date = current_timestamp, name = 'Front area/ Parks/ Open spaces are well maintained', means_of_verification = 'Check that wild vegetation does not exist. Shrubs and Trees are well maintained. Over grown branches of plants/ tree have been trimmed regularly. Dry leaves and green waste are removed on daily basis.Gardens/ green area are secured with fence' where id = 13502;
update checkpoint set last_modified_date = current_timestamp, name = 'Internal Roads and pathways are even and clean', means_of_verification = 'Check that pathways, corridors, courtyards, etc. are clean and landscaped.' where id = 13503;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC has intact boundary wall and functional gates at entry', means_of_verification = 'Check that there is a proper boundary wall of adequate height without any breach. Wall is painted in uniform colour' where id = 13504;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC Infrastructure is well maintained', means_of_verification = 'No major cracks, seepage, chipped plaster & floors in the PHC. Periodic Maintenance is done.' where id = 13505;
update checkpoint set last_modified_date = current_timestamp, name = 'Patients'' furniture is in good condition', means_of_verification = 'Check that Patient beds, examination couch, stool, etc. are not rusted and are painted. Mattresses are clean and not tornTrolleys, Stretchers, Wheel Chairs, etc. are well maintained( As applicable)' where id = 13506;
update checkpoint set last_modified_date = current_timestamp, name = 'Window and doors are maintained', means_of_verification = 'Check, if Window panes are intact, and provided with Grill/ Wire Meshwork. Doors are intact and painted /varnished' where id = 13507;
update checkpoint set last_modified_date = current_timestamp, name = 'No dirt/Grease/Stains/ Garbage in Toilets', means_of_verification = 'Check the toilets randomly for any visible dirt, grease, stains, water accumulation in toiletsCheck for any foul smell in the Toilets' where id = 13508;
update checkpoint set last_modified_date = current_timestamp, name = 'Toilets have running water and functional cistern', means_of_verification = 'Ask cleaning staff to operate cistern and water taps' where id = 13509;
update checkpoint set last_modified_date = current_timestamp, name = 'Ambulatory area are cleaned at least once in the day with wet mop', means_of_verification = 'Ask cleaning staff about frequency of cleaning in a day.' where id = 13510;
update checkpoint set last_modified_date = current_timestamp, name = 'No dirt/Grease/Stains and Cobwebs/Bird Nest/ Dust/ vegetation on walls and roof in Auxiliary area', means_of_verification = 'Check that floors and walls of office, Meeting Room, Staff Room Record room etc. (As applicable) for any visible or tangible dirt, grease, stains, etc.Check roof, walls, corners of these area for any Cobweb, Bird Nest, Vegetation, etc.' where id = 13511;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of Detergent Disinfectant solution / Hospital Grade Phenyl for Cleaning purpose', means_of_verification = 'Check for good quality PHC cleaning solution preferably a ISI mark. Composition and concentration of solution is written on label. Check with cleaning staff if they are getting adequate supply. Verify the consumption records.Check, if the cleaning staff is aware of correct concentration and dilution method for preparing cleaning solution.' where id = 13512;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of Cleaning Equipment', means_of_verification = 'Check the availability of mops, brooms, collection buckets etc. as per requirement.' where id = 13513;
update checkpoint set last_modified_date = current_timestamp, name = 'No dirt/Grease/Stains and Cobwebs/Bird Nest/ Vegetation/ Dust on the walls and roof in the Circulation area', means_of_verification = 'Check that floors and walls of Corridors, Waiting area, stairs, roof top for any visible or tangible dirt, grease, stains, etc.Check that roof, walls, corners of Corridors, Waiting area, stairs, roof top for any Cobweb, Bird Nest, etc.' where id = 13514;
update checkpoint set last_modified_date = current_timestamp, name = 'Corridors are cleaned at least once in the day with wet mop', means_of_verification = 'Ask cleaning staff about frequency of cleaning in a day.' where id = 13515;
update checkpoint set last_modified_date = current_timestamp, name = 'Procedure area are cleaned at least twice in a day', means_of_verification = 'Ask cleaning staff about frequency of cleaning in a day and also verify with check-list' where id = 13516;
update checkpoint set last_modified_date = current_timestamp, name = 'No dirt/Grease/Stains and Cobwebs/Bird Nest/ Dust/ vegetation''s on walls and roof in Procedure area', means_of_verification = 'Check that floors and walls of Procedure area like Labour Room, Dressing Room, Immunization Room etc. (As Applicable) for any visible or tangible dirt, grease, stains, etc.Check that roof, walls, corners of these area for any Cobweb, Bird Nest, Vegetation, etc.' where id = 13517;
update checkpoint set last_modified_date = current_timestamp, name = 'Lab and Pharmacy area are cleaned at least once in the day with wet mop', means_of_verification = 'Ask cleaning staff about frequency of cleaning in a day and also verify with check-list' where id = 13518;
update checkpoint set last_modified_date = current_timestamp, name = 'No dirt/Grease/Stains and Cobwebs/Bird Nest/ Dust/ Vegetation on walls and roof in Lab and Pharmacy area', means_of_verification = 'Check that floors and walls of Lab and Pharmacy for any visible or tangible dirt, grease, stains, etc.Check roof, walls, corners of these area for any Cobweb, Bird Nest, Vegetation, etc.' where id = 13519;
update checkpoint set last_modified_date = current_timestamp, name = 'Periodic Monitoring of Housekeeping activities', means_of_verification = 'Periodic Monitoring is done by MOIC or another designated staff.' where id = 13520;
update checkpoint set last_modified_date = current_timestamp, name = 'Use of Housekeeping Checklist', means_of_verification = 'Check that Housekeeping Checklist is displayed in PHC and updated. Check Housekeeping records if checklists are daily updated for at least last one month' where id = 13521;
update checkpoint set last_modified_date = current_timestamp, name = 'No blocked/ over-flowing drains in the facility', means_of_verification = 'Observe that the drains are not overflowing or blockedAll the drains are cleaned once in a week' where id = 13522;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of connection with Municipal Sewage System/ or Soak Pit', means_of_verification = 'Check if PHC sewage has proper connection with municipal drainage system. If access to municipal system is not accessible, PHC should have a functional septic tank within the premises.' where id = 13523;
update checkpoint set last_modified_date = current_timestamp, name = 'Use of Three bucket system for cleaning', means_of_verification = 'Check if cleaning staff uses three bucket system for cleaning. One bucket for Cleaning solution, second for plain water and third one for wringing the mop. Ask the cleaning staff about the process, Disinfection and washing of mops after every cleaning cycle' where id = 13524;
update checkpoint set last_modified_date = current_timestamp, name = 'Use unidirectional method and out word mopping', means_of_verification = 'Ask cleaning staff to demonstrate the how they apply mop on floors. It should be in one direction without returning to the starting point. The mop should move from inner area to outer area of the room. Separate mop is used in the procedure area.' where id = 13525;
update checkpoint set last_modified_date = current_timestamp, name = 'No dirt/Grease/Stains and Cobwebs/Bird Nest/ Dust/ Vegetation''s on walls and roof in OPD', means_of_verification = 'Check floors and walls of the OPD for any visible or tangible dirt, grease, stains, etc.Check that roof, walls, corners of OPD for any Cobweb, Bird Nest, vegetation, etc.' where id = 13526;
update checkpoint set last_modified_date = current_timestamp, name = 'OPD are cleaned at least twice in a day with wet mop', means_of_verification = 'Ask cleaning staff about frequency of cleaning in a day.' where id = 13527;
update checkpoint set last_modified_date = current_timestamp, name = 'Segregation of BMW is done as per BMW management rule,2016 & amendment', means_of_verification = 'Check that Soiled Waste is collected in the yellow bin & bag.General & Biomedical Waste are not mixed together. Display of work instructions for segregation and handling of Biomedical waste' where id = 13528;
update checkpoint set last_modified_date = current_timestamp, name = 'Check if the staff is aware of segregation protocols', means_of_verification = 'Ask staff about the segregation protocol (Red bag for re-cyclable, Glassware into puncture proof and leak proof boxes and container with blue marking, etc.)' where id = 13529;
update checkpoint set last_modified_date = current_timestamp, name = 'The waste is transported in closed bag & trolley', means_of_verification = 'Check availability of trolley for transportation to collection point.' where id = 13530;
update checkpoint set last_modified_date = current_timestamp, name = 'The PHC''s waste is collected and transported by CWTF operator', means_of_verification = 'Check for records of linkage with CWTF operator or has functional deep burial pits within the facility.' where id = 13531;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of Mercury Spill Management Kit and Staff is aware of Mercury Spill management', means_of_verification = 'Check for Mercury Spill Management Kit and ask staff what he/she would do in case of Mercury spill. (If facility is mercury free, give full compliance)' where id = 13532;
update checkpoint set last_modified_date = current_timestamp, name = 'Disposal of hazardous chemicals', means_of_verification = 'Hazardous chemicals like Glutaraldehyde, Lab Reagents Should not be drained in sewage untreated' where id = 13533;
update checkpoint set last_modified_date = current_timestamp, name = 'Dedicated Storage facility is available for biomedical waste', means_of_verification = 'Check if PHC has dedicated room for storage of Biomedical waste before disposal/handing over to Common Treatment Facility.' where id = 13534;
update checkpoint set last_modified_date = current_timestamp, name = 'No Biomedical waste is stored for more than 48 Hours', means_of_verification = 'Verify that the waste is being disposed / handed over to CTF within 48 hour of generation. Check the record especially during holidays' where id = 13535;
update checkpoint set last_modified_date = current_timestamp, name = 'The facility has treatment facility for managing infectious liquid waste', means_of_verification = 'Check the availability of effluent treatment system.' where id = 13536;
update checkpoint set last_modified_date = current_timestamp, name = 'The laboratory has a functional protocol for managing discarded samples', means_of_verification = 'A copy of such protocol should be available and staff should be aware of the same.' where id = 13537;
update checkpoint set last_modified_date = current_timestamp, name = 'Disposal of General Waste', means_of_verification = 'There is a mechanism of removal of general waste from the facility and its disposal.' where id = 13538;
update checkpoint set last_modified_date = current_timestamp, name = 'Innovations in managing general waste', means_of_verification = 'Look for efforts of the health facility in managing General Waste, such as Recycling of paper waste, vermicomposting, waste to energy initiative, etc.' where id = 13539;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of Bins and plastic bags for segregation of waste at point of use', means_of_verification = 'One set of appropriate size bins at each point of generation for Biomedical and General waste.Check all the bins are provided with chlorine free plastic bags. Ask staff about adequacy of supply.' where id = 13540;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of Needle/ Hub cutter and puncture proof boxes', means_of_verification = 'At each point of generation of sharp waste' where id = 13541;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC has a valid authorization for Bio Medical waste Management from the prescribed authority', means_of_verification = 'Check for the validity of authorization certificate' where id = 13542;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC maintains records, as required under the Biomedical Waste Rules 2016 & amendment', means_of_verification = 'Check following records -a. Annual report submission (before 30th June) b. Yearly Health Check-up record of all handlersc. BMW training records of all staff (once in year training)d. Immunisation records of all waste handlers' where id = 13543;
update checkpoint set last_modified_date = current_timestamp, name = 'Sharp Waste is stored in Puncture proof containers', means_of_verification = 'Check availability of Puncture & leak proof container (White Translucent) at point of use for storing needles, syringes with fixed needles, needles from cutter/burner, scalpel blade, etc.' where id = 13544;
update checkpoint set last_modified_date = current_timestamp, name = 'Disinfection of Broken / Discarded Glassware is done as per recommended procedure', means_of_verification = 'Check if such waste is either pre-treated with 1-2% Sodium Hypochlorite (having 30% residual chlorine) for 20 minutes or by autoclaving/ microwave/ hydroclave, followed storage in puncture proof and leak proof boxes or containers for re-cycling.' where id = 13545;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC has adequate facility for disposal of Biomedical waste', means_of_verification = 'The Health facility within 75 KM of CTF shall have a valid contract with a Common Treatment facility for disposal of Bio medical waste. Or else facility should have Deep Burial Pit and Sharp Pit within premises of Health facility. Such deep burial pit should have approval of the Prescribed Authority and would meet the norms.' where id = 13546;
update checkpoint set last_modified_date = current_timestamp, name = 'Facility manages recyclable waste as per approved procedure', means_of_verification = 'Check management of IV Bottles (Plastic), IV tubes, Urine Bags, Syringes, Catheter, etc.(Autoclaving/ Microwaving/ Hydroclaving followed by shredding or a combination of sterilisation and shredding. Later treated waste is handed over to registered vendors.)' where id = 13547;
update checkpoint set last_modified_date = current_timestamp, name = 'Staff is adheres to hand washing protocol', means_of_verification = 'Check Display of Hand washing InstructionsAsk facility staff to demonstrate 6 steps of normal hand wash and 5 moments of hand washing' where id = 13548;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of Sink and running water at point of use', means_of_verification = 'Check for washbasin with functional tap, soap and running water at all points of use' where id = 13549;
update checkpoint set last_modified_date = current_timestamp, name = 'Decontamination of instruments and Surfaces like examination table, dressing tables etc.', means_of_verification = 'Check whether instruments are decontaminated with 0.5% chlorine solution for 10 minutes. Check instruments are cleaned thoroughly with water and soap before sterilizationAsk staff when and how they clean the surfaces either by chlorine solution or Disinfectant like carbolic acid' where id = 13550;
update checkpoint set last_modified_date = current_timestamp, name = 'Staff knows how to make Chlorine solution', means_of_verification = 'Ask the staff about the procedure of making chlorine solution and its frequency' where id = 13551;
update checkpoint set last_modified_date = current_timestamp, name = 'Antibiotic Policy is implemented at the facility', means_of_verification = 'Check if the PHC has documented Anti biotic policy and doctors are aware of it.' where id = 13552;
update checkpoint set last_modified_date = current_timestamp, name = 'Immunization and medical check-up of Service Providers', means_of_verification = 'PHC staff has been immunized against Hepatitis BCheck for the records and lab investigations of staff' where id = 13553;
update checkpoint set last_modified_date = current_timestamp, name = 'Facility measures the Health care associated infections', means_of_verification = 'Check for monitoring of Healthcare Associated Infection that may occur in a Primary healthcare setting like Injection abscess, Postpartum sepsis, infection at dressing and suturing sites etc.' where id = 13554;
update checkpoint set last_modified_date = current_timestamp, name = 'Facility reports all notifiable diseases and events', means_of_verification = 'Check facility has list of all notifiable disease needs immediate/periodic reporting to higher authority.Check records that notifiable disease have been reported in program such as IDSP and AEFI Surveillance.' where id = 13555;
update checkpoint set last_modified_date = current_timestamp, name = 'Adherence to Protocol for High Level disinfection', means_of_verification = 'Check with the staff process about of High Level disinfection using Boiling for 20 minutes with lid on, soaking in 2% Glutaraldehyde/Chlorine solution for 20 minutes.' where id = 13556;
update checkpoint set last_modified_date = current_timestamp, name = 'Adherence to Protocols for sterilization', means_of_verification = 'Check about awareness of recommended temperature, duration and pressure for autoclaving instruments - 121 degree C, 15 Pound Pressure for 20 Minutes (30 Minutes if wrapped) Linen - 121 C, 15 Pound for 30 Minutes.Check if the staff know the protocol for sterilization of laparoscope soaking it in 2% Glutaraldehyde solution for 10 Hours' where id = 13557;
update checkpoint set last_modified_date = current_timestamp, name = 'Staff is aware of how to manage spills', means_of_verification = 'Check for adherence to protocols' where id = 13558;
update checkpoint set last_modified_date = current_timestamp, name = 'Spill management protocols are displayed at points if use', means_of_verification = 'Check for display' where id = 13559;
update checkpoint set last_modified_date = current_timestamp, name = 'Cross-ventilation', means_of_verification = 'Check availability of Fans/ air conditioning/ Heating/ exhaust/ Ventilators as per environment condition and requirement' where id = 13560;
update checkpoint set last_modified_date = current_timestamp, name = 'Preventive measures for air borne infections has been taken', means_of_verification = 'Check staff is aware, adhere and promote respiratory hygiene and cough etiquettes' where id = 13561;
update checkpoint set last_modified_date = current_timestamp, name = 'Use of Masks ,Head cap and Lab coat, Apron etc.', means_of_verification = 'Check, if staff uses mask head caps , Lab coat and aprons as applicable' where id = 13562;
update checkpoint set last_modified_date = current_timestamp, name = 'Use of Gloves during procedures and examination', means_of_verification = 'Check, if the staff uses gloves during examination, and while conducting procedures' where id = 13563;
update checkpoint set last_modified_date = current_timestamp, name = 'Staff is aware about Standard Precautions', means_of_verification = 'Ask staff about Standard precautions and how they adhere to it.' where id = 13564;
update checkpoint set last_modified_date = current_timestamp, name = 'Infectious patients are separated from other patients', means_of_verification = 'Check patients with respiratory infectious cases are separated from general patients in OPD area' where id = 13565;
update checkpoint set last_modified_date = current_timestamp, name = 'No re-use of disposable personal protective equipment', means_of_verification = 'Check that disposable gloves and mask are not re-used. Reusable Gloves and mask are used after adequate sterilization.' where id = 13566;
update checkpoint set last_modified_date = current_timestamp, name = 'The staff is aware of use of gloves, when to use (occasion) and its type', means_of_verification = 'Check with the staff when do they wear gloves, and when gloves are not required. The Staff should also know difference between clean & sterilized gloves and when to use' where id = 13567;
update checkpoint set last_modified_date = current_timestamp, name = 'Arrangements for washing linens', means_of_verification = 'Check facility has in-house or outsourced arrangements for washing linens at least once in a week.' where id = 13568;
update checkpoint set last_modified_date = current_timestamp, name = 'Available linens are clean', means_of_verification = 'Check linen such as table cloth, bedsheets, curtains etc. are clean and spotless' where id = 13569;
update checkpoint set last_modified_date = current_timestamp, name = 'Medicines are arranged systematically', means_of_verification = 'Check all the shelves/racks containing medicines are labelled in pharmacy and drug storeHeavy items are stored at lower shelves/racksFragile items are not stored at the edges of the shelvesDrugs and consumables are stored away from water and sources of heat, direct sunlight etc.Drugs are not stored at floor and adjacent to wall' where id = 13570;
update checkpoint set last_modified_date = current_timestamp, name = 'Cold storage equipment''s are clean and managed properly', means_of_verification = 'Check ILR, Deep freezers, Refrigerators and Ice packs are cleanCheck if there is a practice of regular cleaning.Cold storage equipment are not been used for purpose other than storing drugs and vaccines.' where id = 13571;
update checkpoint set last_modified_date = current_timestamp, name = 'Presence of security Guard', means_of_verification = 'Check for the presence of at least one security personnel at PHC' where id = 13572;
update checkpoint set last_modified_date = current_timestamp, name = 'Departments are locked after working hours', means_of_verification = 'Departments like OPD, Lab, Administrative office etc. are locked after working hours.' where id = 13573;
update checkpoint set last_modified_date = current_timestamp, name = 'The facility receives adequate quantity of water as per requirement', means_of_verification = 'Water is available on 24x7 basis at all points of usage' where id = 13574;
update checkpoint set last_modified_date = current_timestamp, name = 'There is storage tank for the water and tank is cleaned periodically', means_of_verification = 'The PHC should have capacity to store 48 hours water requirement Water tank is cleaned at six monthly interval and records are maintained.' where id = 13575;
update checkpoint set last_modified_date = current_timestamp, name = 'Biomedical waste generated during outreach session are transported to the PHC on the same day', means_of_verification = 'Check the records and ask staff' where id = 13576;
update checkpoint set last_modified_date = current_timestamp, name = 'Medical officers monitor cleanliness and hygiene of outreach sessions and sub centres.', means_of_verification = 'Check with medical officers and records of monthly meeting ''''swachh bharat abhiyan'''' has been followed up during monthly meetings with extension workers like MPW, ASHA, ANM etc.' where id = 13577;
update checkpoint set last_modified_date = current_timestamp, name = 'IEC regarding Swachhta Abhiyan is displayed within the facilities’ premises', means_of_verification = 'Should be displayed prominently in local language' where id = 13578;
update checkpoint set last_modified_date = current_timestamp, name = 'IEC regarding importance of Hygiene practices are displayed', means_of_verification = 'Check IEC regarding hand washing, water sanitation, use of toilets are displayed in local language' where id = 13579;
update checkpoint set last_modified_date = current_timestamp, name = 'Roles and responsibility of different staff members have been assigned and communicated', means_of_verification = 'Ask different members about their roles and responsibilities' where id = 13580;
update checkpoint set last_modified_date = current_timestamp, name = 'Cleanliness and infection control committee has been constituted', means_of_verification = 'Verify with the records' where id = 13581;
update checkpoint set last_modified_date = current_timestamp, name = 'There is a regular monitoring of hygiene of staff', means_of_verification = 'Check about personal hygiene and clean dress of staff' where id = 13582;
update checkpoint set last_modified_date = current_timestamp, name = 'PHC has dress code policy for all cadre of staff', means_of_verification = 'PHCs staff adhere to dress codeCheck Identity cards and name plates have been provided to all staff' where id = 13583;
update checkpoint set last_modified_date = current_timestamp, name = 'Bio medical waste Management training has been provided to the staff', means_of_verification = 'Verify with the training records' where id = 13584;
update checkpoint set last_modified_date = current_timestamp, name = 'Infection control Training has been provided to the staff', means_of_verification = 'Check staff are trained at the time of induction and at least once in every year' where id = 13585;
update checkpoint set last_modified_date = current_timestamp, name = 'Patients are made aware of their responsibility of keeping the health facility clean', means_of_verification = 'Ask patients about their roles & responsibilities with regards to cleanliness. Patient’s responsibilities should be prominently displayed' where id = 13586;
update checkpoint set last_modified_date = current_timestamp, name = 'The Health facility has a system to take feed-back from patients and visitors for maintaining the cleanliness of the facility', means_of_verification = 'Check if there is a feedback system for the patients. Verify the records' where id = 13587;
update checkpoint set last_modified_date = current_timestamp, name = 'Regular repairs and maintained of roads, footpaths and pavements', means_of_verification = 'Check when was the last repair done, details of the repair and current condition of the road- pot-holes, broken footpath etc.' where id = 15137;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of garbage storage area', means_of_verification = 'Garbage storage area is away from residential/commercial areas and is covered/fenced. It is not causing public nuisance.' where id = 15138;
update checkpoint set last_modified_date = current_timestamp, name = 'Innovations in managing waste', means_of_verification = 'Check, if certain innovative practices have been introduced for managing general waste e.g. Vermicomposting, Re-cycling of papers, Waste to energy, Compost Activators, etc.' where id = 15139;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of bins for General recyclable and biodegradable wastes', means_of_verification = 'Check availability adequate number of bins for Biodegradable and recyclable general waste in the nearby market' where id = 15140;
update checkpoint set last_modified_date = current_timestamp, name = 'Surrounding areas are well maintained', means_of_verification = 'Check that there is no over grown shrubs, weeds, grass, potholes, bumps etc. in surrounding areas' where id = 15141;
update checkpoint set last_modified_date = current_timestamp, name = 'No loose hanging wires in and around the bill boards, electrical poles, etc.', means_of_verification = 'Check for any loose hanging wires.' where id = 15142;
update checkpoint set last_modified_date = current_timestamp, name = 'Parks and green areas of surrounding area are well maintained', means_of_verification = 'Check that there no wild vegetation & growth in the surroundings. Shrubs and trees are well maintained. Dry leaves and green waste are removed regularly.' where id = 15143;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of public toilets in surrounding area', means_of_verification = 'Check for separate toilets for male and female and they are conveniently located and clean.' where id = 15144;
update checkpoint set last_modified_date = current_timestamp, name = 'Availability of adequate parking stand in surrounding area', means_of_verification = 'Check for parking stand for auto/ rickshaw/taxi etc., and they are not parked haphazardly.' where id = 15145;
update checkpoint set last_modified_date = current_timestamp, name = 'No unwanted/broken/ torn / loose hanging posters/ billboards.', means_of_verification = 'Check that hospital surrounding are not studded with irrelevant and out dated posters, slogans, wall writings, graffiti, etc.' where id = 15146;
update checkpoint set last_modified_date = current_timestamp, name = 'Area around the facility is clean, neat & tidy', means_of_verification = 'Check for any litter/garbage/refuse and water logging in the surrounding area of the facility.' where id = 15147;
