update checkpoint set last_modified_date = current_timestamp, name = 'The Staff uses needle cutters for cutting/burning the syringe hub', means_of_verification = 'Observe that needle cutters are available at every point of waste generation and also being used' where id = 13202;
update checkpoint set last_modified_date = current_timestamp, name = 'Discarded / contaminated linen is disposed as per procedure given in the BMW Rules 2016', means_of_verification = 'Check that discarded linen, mattresses & bedding contaminated with blood or body fluid is subjected to disinfection by non-chlorinated disinfection (e.g. Hydrogen Peroxide) followed by incineration.
