update checkpoint set last_modified_date = current_timestamp, inactive = FALSE, sort_order = 0, am_observation = FALSE, am_staff_interview = TRUE, am_patient_interview = FALSE, am_record_review = TRUE, name = 'Management and follow up of PIH/Eclampsia \Pre Eclampsia ', means_of_verification = 'Monitors BP in every case, and tests for proteinuria if BP is >140/90 mmHg
