insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management of 2nd stage of labour:', 'Allows the spontaneous delivery of head , gives Perineal support and assist in delivering baby. Check progress is recorded on partograph', TRUE, TRUE, FALSE, FALSE, (select max(id) from measurable_element where reference = 'E17.1'), (select max(id) from checklist where name = 'Labour Room'));
