insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Use of Uterotonic Drugs', 'Administration of 10 IU of oxytocin IM with in 1 minute of Birth', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.1'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Control Cord Traction', 'Only during Contraction', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.1'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Uterine Massage', 'After placenta expulsion , Checks Placenta & Membranes for Completeness', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.1'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Staff is aware of Indications for referring patient for Surgical Intervention', 'Ask staff how they identify slow progress of labour , How they interpret Partogram', FALSE, TRUE, FALSE, FALSE, (select max(id) from measurable_element where reference = 'E17.2'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management and follow up of PIH/Eclampsia \Pre Eclampsia', 'Monitors BP in every case, and tests for proteinuria if BP is >140/90 mmHg If BP is 140/90 mmHg or more with proteinuria 2+ along with any two of the following danger signs: severe headache, blurring of vision, severe pain abdomen or reduced urine output, BP > 160/110 or more with proteinuria 3+; OR in cases of Eclampsiaadministers loading dose of Magnesium Sulphate (MgSO4) and refers/ calls for specialist attention; continues maintenance dose of MgSO4- 5 g of MgSO4 IM in alternate buttocks every four hours, for 24 hours after birth/last convulsion, whichever is laterIf BP is >160/110 mmHg or more, give appropriate anti-hypertensive (Hydralazine/Methyl Dopa/ Nifedipine)', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management of Postpartum Haemorrhage', 'Assessment of bleeding (PPH if >500 ml or > 1 pad soaked in 5 Minutes. IV Fluid, bladder catheterization, measurement of urine output, Administration of 20 IU of Oxytocin in 500 ml Normal Saline or RL at 40-60 drops per minute . Performs Bimanual Compression of Uterus', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management of Retained Placenta', 'Administration of another dose of Oxytocin 20IU in 500 ml of RL at 40-60 drops/min an attempt to deliver placenta with repeat controlled cord traction. If this fails performs manual removal of Placenta', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management of Uterine Atony', 'Vigorous Uterine massage, gives Oxytocin 20 IU in 500 ml of R/L 40 to 60 drops/minute (Continue to administer Oxytocin uptown maximum of 3 litres of solution with Oxytocin) If still bleeding perform bi manual uterine compression with palpation of femoral pulse', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management of Obstructed Labour', 'Diagnose obstructed labour based on data registered from the partograph, Re-hydrate the patient to maintain normal plasma volume, check vitals, give broad spectrum antibiotics, perform bladder catheterization and take blood for Hb & grouping, Decide on the mode of delivery as per the condition of mother and the baby', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Management of Puerperal sepsis', 'Diagnose puerperal sepsis based on clinical criteria: continuous fever for at least 24 hours or recurring within the first 10 days after delivery, increased pulse rate, increased respiration, offensive/foul smelling lochia, sub involution of the uterus, headache and general malaise, pelvic pain, pain, swelling and pus discharge from laceration or episiotomy or incision. Conduct appropriate lab. investigations, Prescribes IV fluids and broad spectrum antibiotics for seven days & advises perennial care', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
insert into checkpoint (name, means_of_verification, am_observation, am_staff_interview, am_patient_interview, am_record_review, measurable_element_id, checklist_id) values ('Delivery of infectious cases HIV positive PW', '', FALSE, TRUE, FALSE, TRUE, (select max(id) from measurable_element where reference = 'E17.3'), (select max(id) from checklist where name = 'Labour Room'));
